local jukeboxmute = CreateClientConVar( "jukebox_mute", 1, true, false )function PlaySong( song )	if HTMLJUKEBOX then		HTMLJUKEBOX:Remove( )	end		if not jukeboxmute:GetBool() == true then return end		HTMLJUKEBOX = vgui.Create( "HTML" )	HTMLJUKEBOX:SetSize( 0 , 0 )	HTMLJUKEBOX:OpenURL( song )	HTMLJUKEBOX:Refresh(true)	HTMLJUKEBOX:StartAnimate(100)endusermessage.Hook( "jukebox_play", function( um )	local song = um:ReadString(  )		PlaySong( song )end)function CreateJukeBox( )	MakeBox( )endfunction MakeBox( )	if not LocalPlayer( ).PlayerData then return end	if not LocalPlayer( ).PlayerData["money"] then return end		local w = ScrW( )	local h = ScrH( )		local function AddText( P, x, y, Text, Font, Color )        local label = vgui.Create("DLabel", P )        label:SetPos( x, y )        label:SetText( Text or "" )        label:SetFont( Font )        label:SetTextColor( Color )        label:SizeToContents()    end		local Frame = vgui.Create( "DFrame" )	Frame:SetSize( w / 2 + 200, h / 2 + 300 )	Frame:Center()	Frame:SetTitle( "" )	Frame:SetDraggable( false )	Frame:ShowCloseButton( false )	Frame:MakePopup( )		Frame.Paint = function( )		Derma_DrawBackgroundBlur( Frame, 1 )				surface.SetDrawColor( 255, 255, 255, 255 )		surface.DrawLine( 15, Frame:GetTall( ) - 100, Frame:GetWide( ) - 15, Frame:GetTall( ) - 100 )				draw.SimpleText( "Playing a song will cost 100.", "Trebuchet24", 15, Frame:GetTall( ) - 95, color_white )		draw.SimpleText( "Made by AC2", "Trebuchet24", 15, Frame:GetTall( ) - 75, color_white )	end		local close = vgui.Create( "Shop_Button", Frame )	close:SetPos( Frame:GetWide( ) - 150,  Frame:GetTall( ) - 100 )	close:SetSize( 150, 75 )	close:Text( "Close" )	close.DoClick = function( )		Frame:Close( )	end		local list = vgui.Create( "DListView", Frame )	list:SetPos( 15, 15 )	list:SetSize( Frame:GetWide( ) - 30, Frame:GetTall( ) - 165 )	list:AddColumn( "Song" ):SetFixedWidth( Frame:GetWide( ) - 200 )	list:AddColumn( "Time" )		list.Paint = function( ) end		for i = 1, #JUKEBOX.SONGS do		list:AddLine( JUKEBOX.SONGS[i].Name, JUKEBOX.SONGS[i].Time )	end		local play = vgui.Create( "Shop_Button", Frame )	play:SetPos( 5, Frame:GetTall( ) - 150 )	play:SetSize( 150, 40 )	play:Text( "Play" )	play.DoClick = function( )		if not (list:GetSelectedLine( ) == nil) then			RunConsoleCommand( "jukebox_buy", JUKEBOX.SONGS[list:GetSelectedLine( )].ID )		end		Frame:Close( )	end		local stop = vgui.Create( "Shop_Button", Frame )	stop:SetPos( Frame:GetWide( ) - 300, Frame:GetTall( ) - 150 )	stop:SetSize( 150, 40 )	stop:Text( "Stop" )	stop.DoClick = function( )		if HTMLJUKEBOX then			HTMLJUKEBOX:Remove( )		end				Frame:Close( )	end		local mute = vgui.Create( "Shop_Button", Frame )	mute:SetPos( Frame:GetWide( ) - 150, Frame:GetTall( ) - 150 )	mute:SetSize( 150, 40 )		if jukeboxmute:GetBool( ) == true then		mute:Text( "Sound: On" )	else		mute:Text( "Sound: Off" )	end		mute.DoClick = function( )		if HTMLJUKEBOX then			HTMLJUKEBOX:Remove( )		end				if jukeboxmute:GetBool( ) == true then			RunConsoleCommand( "jukebox_mute", 0 )		else			RunConsoleCommand( "jukebox_mute", 1 )		end				Frame:Close( )	endend