local PANEL = {}function PANEL:Init( )	self:SetWide( 110 )	self:SetTall( 20 )		self.Visible = false	self.Text = ""endfunction PANEL:SetText( text )	self.Text = text	surface.SetFont( "ChatFont" )	local fontwidth, fontheight = surface.GetTextSize( self.Text )		self:SetWide( fontwidth + 15 )endfunction PANEL:Open( )	self.Visible = trueendfunction PANEL:Close( )	self.Visible = false endfunction PANEL:Paint( )	if ( self.StartShowingAt && SysTime() < self.StartShowingAt ) then return false end	surface.SetFont( "ChatFont" )	local fontwidth, fontheight = surface.GetTextSize( self.Text )		surface.SetDrawColor( 0, 0, 0, 180 )	surface.DrawRect( (self:GetWide( ) / 2) - ( fontwidth / 2 ) - 15, 0, fontwidth + 30 + self:GetWide( ), self:GetTall( ) )	--surface.SetDrawColor( 255, 255, 255, 255 )	--surface.DrawOutlinedRect( (self:GetWide( ) / 2) - ( fontwidth / 2 ) - 10, 0, fontwidth + 20 + self:GetWide( ), self:GetTall( ) )			draw.SimpleText( self.Text, "ChatFont", self:GetWide( ) / 2, self:GetTall( ) / 2, color_white, TEXT_ALIGN_CENTER, TEXT_ALIGN_CENTER )endfunction PANEL:GetPos( panel )	local x, y = panel:LocalToScreen( 0, 0 )	local w, h = panel:GetSize()		self.ArrowTall = self:GetTall( )	self.ArrowWide = self:GetWide( )		self.Rot = 0	local ty = y - self:GetTall() - 16	self.ArrowPosY = self:GetTall()	if ( ty < 0 ) then			ty = y + h + 16		self.ArrowPosY = 0		self.ArrowTall = self.ArrowTall * -1		end		local tx = x + panel:GetWide() * 0.5	self.ArrowPosX = panel:GetWide() * 0.1	if ( tx + self:GetWide() > ScrW() ) then			tx = x + panel:GetWide() * 0.5 - self:GetWide()		self.ArrowPosX = self:GetWide() * 0.9		self.ArrowWide = self.ArrowWide * -1	end		self:SetPos( tx, ty )	self.StartShowingAt = SysTime() endvgui.Register( "Shop_Tip", PANEL )