local function give( pl, cmd, args )	if not args[1] then return end	if not args[2] then return end	local map = game.GetMap( )	if not pl:IsOwner( ) then return end		if args[1] == "*" then		for k, v in pairs( player.GetAll( ) ) do			target = v			target:Give( args[2] )		end				PrintAll( "ADMIN", "Player <color=red>"..pl:Name( ).."</color> gave <color=red>everyone</color> a <color=red>"..args[2]..".", "gui/silkicons/exclamation" )		return	end		local target = FindPlayersByName( args[1] )		if target == nil then 		pl:ChatPrint( "Nobody was found" )		return	end		for k, v in pairs( target ) do		v:Give( args[2] )	end		PrintAll( "ADMIN", "Player <color=red>"..pl:Name( ).."</color> gave <color=white>player</color> <color=red>"..PlayerList( target ).."</color> a <color=red>"..args[2].."</color>.", "gui/silkicons/exclamation" )endconcommand.Add( "devasion_give", give )chatcommand.Add( "!give", give )