function DEVASION:Print( str )	print( "[DEVASION] "..str )endfunction DEVASION:Notify( ... )	local ply	local arg = { ... }			if ( type( arg[1] ) == "Player" or arg[1] == NULL ) then ply = arg[1] end			if ( ply != NULL ) then		umsg.Start( "DA_NOTIFY", ply )			umsg.Short( #arg )			for _, v in ipairs( arg ) do				if ( type( v ) == "string" ) then					umsg.String( v )				elseif ( type ( v ) == "table" ) then					umsg.Short( v.r )					umsg.Short( v.g )					umsg.Short( v.b )					umsg.Short( v.a )				end			end		umsg.End()	endendfunction PrintAll( type, string, tag )	for k, v in pairs( player.GetAll( ) ) do		if type == "ADMIN" then			v:ChatPrint( string, tag or "gui/silkicons/world" )		else			v:PrintMessage( type, string, tag or "gui/silkicons/world" )		end	endend