if ( !require( "furryfinder" ) ) then return end local playerMeta = FindMetaTable( "Player" )if ( !playerMeta ) then return end local function GetPlayerBySteamID( steamid )    for _, v in ipairs( player.GetAll() ) do        if ( v:SteamID() == steamid ) then            return v        end    end    return nilend function playerMeta:RequestGroupStatus( group )    return furryfinder.RequestGroupStatus( self:SteamID(), group )end function playerMeta:IsGroupMember( group )    if ( !self.GroupStatus || !self.GroupStatus[ group ] ) then        return nil // unknown case, haven't requested yet    end         return self.GroupStatus[ group ].IsMemberend function playerMeta:IsGroupOfficer( group )    if ( !self.GroupStatus || !self.GroupStatus[ group ] ) then        return nil // unknown case, haven't requested yet    end         return self.GroupStatus[ group ].IsOfficerend hook.Add( "PlayerAuthed", "GroupPlayerAuthed", function( ply, steam, uniqueid )    ply:RequestGroupStatus( STEAM_GROUP_ID )end ) 