GUNGAME = {}GUNGAME.Weapons = {}GUNGAME.Weapons[0] = "weapon_mg_p228"GUNGAME.Weapons[2] = "weapon_mg_deagle"GUNGAME.Weapons[4] = "weapon_mg_dual"GUNGAME.Weapons[6] = "weapon_mg_m3"GUNGAME.Weapons[8] = "weapon_mg_m1014"GUNGAME.Weapons[10] = "weapon_mg_mp5"GUNGAME.Weapons[12] = "weapon_mg_ump"GUNGAME.Weapons[14] = "weapon_mg_p90"GUNGAME.Weapons[16] = "weapon_mg_m4a1"GUNGAME.Weapons[18] = "weapon_mg_famas"GUNGAME.Weapons[20] = "weapon_mg_ak47"GUNGAME.Weapons[22] = "weapon_mg_scout"GUNGAME.Weapons[24] = "weapon_mg_awp"GUNGAME.Weapons[26] = "weapon_mg_aug"GUNGAME.Weapons[28] = "weapon_mg_m249"local END = falsefunction GM:GetGGLevel( pl )	return pl.GG_Level or 0endfunction GM:AddGGLevel( pl )	pl.GG_Level = pl.GG_Level + 1		GAMEMODE:GiveGGWeapons( pl )endfunction GM:GiveGGWeapons( pl )	if GAMEMODE:GetGGLevel( pl ) >= 30 and not (END == true) then		PrintAll( HUD_PRINTCENTER, pl:Name( ).." has won this round." )		pl:AddPoints( 5 )		pl:AddMoney( 400 )		GAMEMODE:EndRound( )		END = true		return	end		if END == true then return end		if not GUNGAME.Weapons[GAMEMODE:GetGGLevel( pl )] then		pl:StripWeapons( )		pl:Give( GUNGAME.Weapons[pl.GG_Level - 1] )		return	end		pl:StripWeapons( )	pl:Give( GUNGAME.Weapons[pl.GG_Level] )end